import React from 'react';
import Header from '../Common/Header';
import ImgBackground from '../../assets/img/Group 2218.png';
import { borderRadius, height, width } from '@mui/system';
import { MarginTwoTone } from '@mui/icons-material';

function MainImage() {
    return (
        <>
            <div class="main-image" style={{ backgroundColor: 'white'}}>
                <Header />
                <img style={{ width: '50%',marginLeft:'45%' ,height: '40vw', borderRadius:'10px'}} src={ImgBackground} alt="" />
                <div class="text">
                    <h1>Top Gun Maverick</h1> 
                    <p style={{ fontSize: '17px', marginTop:'2vw', width: '30%'}}>
                    Top Gun: Maverick is a 2022 American action drama 
                    film directed by Joseph Kosinski and written by Ehren Kruger, Eric Warren Singer, and Christopher McQuarrie
                    Thirty years of service leads Maverick 
                    to train a group of elite TOPGUN graduates 
                    to prepare for a high-profile mission while 
                    Maverick battles his past demons.n the film, 
                    Maverick confronts his past while training 
                    a group of younger Top Gun graduates, including
                    the son of his deceased best friend, for a dangerous mission.</p><br />


                   <a href="https://www.youtube.com/watch?v=lr_spVswTUo&vl=en" target="_blank"><button>Watch Now</button></a>
                </div>
            </div>
        </>
    );
}

export default MainImage;
